package ru.vsu.but.project;public class TestFormats {    public static void main (String[] args) {        String s = String.format("%, d", 1000000000);        System.out.println(s);        String s1 = String.format("Мне нужно исправить %.2f ошибки.", 476578.09876);        System.out.println(s1);        String s2 = String.format("Мне нужно исправить %,.2f ошибки.", 476578.09876);        System.out.println(s2);    }}